import React, { Component } from "react";

export function TextInput({handleChange, newItem, item, inputValue})
{

    return(
        <input
        className="newColumn"
        type="text"
        placeholder="Input a New Column and Press Enter"
        maxLength="75"
        onKeyPress={(event) => newItem(event, item.id)}
        value={inputValue}
        onChange={handleChange}
        />
    )
}
