import React, { Component } from "react";
import EdiText from 'react-editext'

export function Card({board, column, card, rloadColumns, newColumn, handleChange, removeColumn, removeCard, modifyCard, inputValue})
{
    return (
        <>
        <li class="board-col-item">
            <EdiText
                showButtonsOnHover
                submitOnEnter
                cancelOnEscape
                editOnViewClick={true}
                type='text'
                value={card.title}
                onSave={(event) => modifyCard(event, card.id)}
            />
            <span className="removeItemButton" onClick={(e) => removeCard(card.id, card.position, card.board_column_id)}>x</span>
        </li>
        </>
    )
}
