function App() {
  return(
    <div className="mainContainer">
        <div className="topHeading">
          <h1>Boards</h1>
        </div>
        {/* <BoardsContainer /> */}
        <Router>
        <nav>
          <ul>
            <li><Link to="/board/:id">Manatee</Link></li>
            <li><Link to="/narwhal">Narwhal</Link></li>
            <li><Link to="/whale">Whale</Link></li>
          </ul>
        </nav>
        <Switch>
          <Route path="/">
            <BoardsContainer />
          </Route>
          <Route path="/board/:id" exact component={Board}>
            <Board />
          </Route>
        </Switch>
      </Router>
      </div>
  );
}

export default App;
