import React, { Component } from "react";
import axios from "axios";
import update from "immutability-helper";
import { Link } from "react-router-dom";

class Board extends Component {
    constructor(props) {
        super(props);
        this.state = {
          columns: [],
        };
    }

    loadColumns() {
        axios
          .get(`/api/v1/boards/18/board_columns`)
          .then((res) => {
            this.setState({ columns: res.data });
          })
          .catch((error) => console.log(error));
    }

    componentDidMount() {
        this.loadColumns();
    }

    render() {
        return (
            <div className="mainContainer">
            <div className="topHeading">
              <h1>boards</h1>
              <div className="wrapItems">
                <ul className="listItems">
                    {this.state.boards.map((board) => {
                    return (
                        <li className="item" board={board} key={board.id}>
                        <Link to={`/board/${board.link}`}>{board.title}</Link>
                        <label className="itemDisplay"></label>
                        <span className="removeItemButton" onClick={(e) => this.removeBoard(board.id)}>
                            x
                        </span>
                        </li>
                    );
                    })}
                </ul>
                </div>
              <Link to="/boards">Back</Link>
            </div>
          </div>
        );
    }


}






export default Board;
