import React, { Component } from "react";
import axios from "axios";
import update from "immutability-helper";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";

class BoardsContainer extends Component {
    constructor(props) {
        super(props);
        this.state = {
          boards: [],
        };
    }

    loadBoards() {
        axios
          .get("/api/v1/boards")
          .then((res) => {
            this.setState({ boards: res.data });
          })
          .catch((error) => console.log(error));
    }

    newBoard = (e) => {
        if (e.key === "Enter" && !(e.target.value === "")) {
          axios
            .post("/api/v1/boards", { board: { title: e.target.value } })
            .then((res) => {
              const boards = update(this.state.boards, {
                $splice: [[0, 0, res.data]],
              });

              this.setState({
                boards: boards,
                inputValue: "",
              });
            })
            .catch((error) => console.log(error));
        }
      };

      handleChange = (e) => {
		this.setState({inputValue: e.target.value});
	  }

      removeBoard = (id) => {
        axios
          .delete(`/api/v1/boards/${id}`)
          .then((res) => {
            const boardIndex = this.state.boards.findIndex((x) => x.id === id);
            const boards = update(this.state.boards, {
              $splice: [[boardIndex, 1]],
            });
            this.setState({
                boards: boards,
            });
          })
          .catch((error) => console.log(error));
      };

    componentDidMount() {
        this.loadBoards();
    }



        render() {
            return (
            <div>
                <div className="taskContainer">
                <input
                    className="newBoard"
                    type="text"
                    placeholder="Input a New Board and Press Enter"
                    maxLength="75"
                    onKeyPress={this.newBoard}
                    value={this.state.inputValue}
                    onChange={this.handleChange}
                />
                </div>
                <div className="wrapItems">
                <ul className="listItems">
                    {this.state.boards.map((board) => {
                    return (
                        <li className="item" board={board} key={board.id}>
                        {/* <input className="itemCheckbox" type="checkbox" /> */}
                        <Link to={`/board/${board.id}`} className="btn custom-button">
                            View Board
                        </Link>
                        <label className="itemDisplay">{board.title}</label>
                        <span className="removeItemButton" onClick={(e) => this.removeBoard(board.id)}>
                            x
                        </span>
                        </li>
                    );
                    })}
                </ul>
                </div>
            </div>
            );
        }
}



export default BoardsContainer;
