import React, { Component } from "react";
import { TextInput } from "./TextInput";
import { Link, useParams} from "react-router-dom";

export function BoardsList({boards, handleChange, newBoard, inputValue, removeBoard})
{
    return (
        <>
        <div>
                <div className="topHeading">
                    <h1>Boards</h1>
                 </div>
                <div className="taskContainer">
                <TextInput 
                handleChange={handleChange}
                newItem={newBoard}
                item={newBoard}
                inputValue={inputValue}
                placeholder="Input a New Board and press Enter"
                className="newColumn"
            />
                </div>
                <div className="wrapItems">
                <ul className="listItems">
                    {boards.map((board) => {
                    return (
                        <li className="item" board={board} key={board.id}>
                        <Link to={`/board/${board.link}`}>{board.title}</Link>
                        <label className="itemDisplay"></label>
                        <span className="removeItemButton" onClick={(e) => removeBoard(board.id)}>
                            x
                        </span>
                        </li>

                    );
                    })}
                </ul>
                </div>
            </div></>
    )
}
