import React, { Component } from "react";
import { TextInputCards } from "./TextInputCards";
import {Card} from "./Card";

export function Column({board, column, cards, rloadColumns, newColumn, newCard, handleChange, removeColumn, removeCard, modifyCard, inputValue})
{
    return (
        <>
        <ul class="board-col">
                  <li class="board-col-name">
                    {column.title}
                    <span className="removeItemButton" onClick={(e) => removeColumn(column.id)}>x</span>
                  </li>
               {cards.sort((a,b) => a.position - b.position).map((card) => {
                    return
                        <Card
                        cards={cards}
                        column={column}
                        removeColumn={removeColumn}
                        newCard={newCard}
                        removeCard={removeCard}
                        handleChange={handleChange}
                        board={board}
                        inputValue={inputValue}
                        modifyCard={modifyCard}
                    />

                  })}
                  <TextInputCards 
                    handleChange={handleChange}
                    newCard={newColumn}
                    board={board}
                    inputValue={inputValue}
                    />
                </ul>
        </>
    )
}
