import React, { Component } from "react";

export function TextInput({handleChange, newColumn, board, inputValue})
{

    handleChange = (e) => {
		this.setState({inputValue: e.target.value});
	  }

    return(
        <input
        className="newColumn"
        type="text"
        placeholder="Input a New Column and Press Enter"
        maxLength="75"
        onKeyPress={(event) => newColumn(event, board.id)}
        value={inputValue}
        onChange={handleChange}
        />
    )
}
