import React, { Component } from "react";
import axios from "axios";

class BoardsContainer extends Component {
    constructor(props) {
        super(props);
        this.state = {
          boards: [],
        };
    }

    loadBoards() {
        axios
          .get("/api/v1/boards")
          .then((res) => {
            this.setState({ boards: res.data });
          })
          .catch((error) => console.log(error));
      }

      componentDidMount() {
        this.loadBoards();
      }


