import React, { Component } from "react";
import axios from "axios";
import update from "immutability-helper";
import { Link } from "react-router-dom";

class Board extends Component {
    constructor(props) {
        super(props);
        this.state = {
          columns: [],
          cards: []
        };
    }

    loadColumns() {
        axios
          .get(`/api/v1/boards/6/board_columns`)
          .then((res) => {
            this.setState({ columns: res.data });
          })
        axios
          .get(`/api/v1/boards/6/cards`)
          .then((res) => {
            this.setState({ cards: res.data });
          })
        .catch((error) => console.log(error));
    }

    reloadColumns = () => {
        this.setState({ columns: [] });
        this.loadBoards();
      };

    removeColumn = (board_id, column_id) => {
        axios 
            .delete(`/api/v1/boards/${board_id}/board_columns/${column_id}`)
            .then((res) => {
                if (res.ok) {
                  this.reloadColumns();
                  return res.json();
                }
                throw new Error("Network error.");
              })
              .catch((error) => console.log(error));
    };

    componentDidMount() {
        this.loadColumns();
    }

    render() {
        return (
            <div className="mainContainer">
            <div className="topHeading">
              <h1>Boards</h1>
              <div className="wrapItems">
                <div className="listItems">
                    {this.state.columns.map((column) => {
                    return (
                        <li className="item" column={column} key={column.board_id}>
                        <Link to="/boards">{column.title}</Link>
                        <label className="itemDisplay"></label>
                        <span className="removeItemButton" onClick={(e) => this.removeColumn(7,12)}>
                            x
                        </span>
                        </li>
                    );
                    })}
                </div>
                <div className="listItems">
                    {this.state.cards.map((card) => {
                    return (
                        <div className="item" card={card} key={card.board_column_id}>
                        <Link to="/boards">{card.title}</Link>
                        <label className="itemDisplay"></label>
                        </div>
                    );
                    })}
                </div>
                </div>
              <Link to="/boards">Back</Link>
            </div>
          </div>
        );
    }


}






export default Board;
