import React, { Component } from "react";
import axios from "axios";
import update from "immutability-helper";
import { Link, useParams} from "react-router-dom";
import { withRouter } from "react-router";
import EdiText from 'react-editext'
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import Sortable from 'sortablejs';

class Board extends Component {
    constructor(props) {
        super(props);
        this.state = {
          columns: [],
          cards: [],
          board: null
        };
    }

    loadColumns() {
        const link = this.props.match.params.link;
        axios 
          .get(`/api/v1/board/${link}`)
          .then((res) => {
            this.setState({ board: res.data });
            axios
            .get(`/api/v1/board_columns`, { params: {board_id: res.data.id} })
            .then((res) => {
              this.setState({ columns: res.data });
              res.data.forEach((column) => {
                axios
                .get(`/api/v1/cards`, { params: {board_column_id: column.id} })
                .then((res) => {
                    const newCards = this.state.cards.concat(res.data)
                    this.setState({cards: newCards})
                  })
                })
              })
            })
    }

    reloadColumns = () => {
        this.setState({ columns: [], cards: [], board: null });
        this.loadColumns();
      };

    newColumn = (e, id) => {
        if (e.key === "Enter" && !(e.target.value === "")) {
            axios
              .post(`/api/v1/board_columns`, { board_column: { title: e.target.value, board_id: id} })
              .then((res) => {
                this.reloadColumns();

                this.setState({
                  inputValue: "",
                });
              })
              .catch((error) => console.log(error));
            }
    };

    newCard = (e, column_id, id) => {
        if (e.key === "Enter" && !(e.target.value === "")) {
            axios
              .post(`/api/v1/cards`, { card: { title: e.target.value, board_column_id: column_id, board_id: id} })
              .then((res) => {
                this.reloadColumns();

                this.setState({
                  inputValue: "",
                });
              })
              .catch((error) => console.log(error));
            }
    };

    handleChange = (e) => {
		this.setState({inputValue: e.target.value});
	  }


    removeColumn = ( id ) => {
        axios 
            .delete(`/api/v1/board_columns/${id}`)
            .then((res) => {
                const columnIndex = this.state.columns.findIndex((x) => x.id === id);
                const columns = update(this.state.columns, {
                  $splice: [[columnIndex, 1]],
                });
                this.setState({
                    columns: columns,
                });
                this.reloadColumns();

              })
              .catch((error) => console.log(error));
    };

    removeCard = ( id ) => {
        axios
            .delete(`/api/v1/cards/${id}`)
            .then((res) => {
                this.reloadColumns();
              })
              .catch((error) => console.log(error));
    };


    componentDidMount() {
        this.loadColumns();
    }


    modifyCard= (value, card_id) => {
        if (!(value === "")) {
            axios
                .put(`/api/v1/cards/${card_id}`, { card: { title: value} })
                .then((res) => {
                    this.reloadColumns();
                })
                .catch((error) => console.log(error));
        }
    };

    render() {
        return (
          <DragDropContext>
          <>
          <div className="taskContainer">
            <input
              className="newColumn"
              type="text"
              placeholder="Input a New Column and Press Enter"
              maxLength="75"
              onKeyPress={(event) => this.newColumn(event, this.state.board.id)}
              value={this.state.inputValue}
              onChange={this.handleChange}
            />
          </div>
            <div class="board">  
            {this.state.columns.map((column) => {
              const Cards = this.state.cards.filter((card) => card.board_column_id === column.id);
              return <>
              <Droppable >
                <ul class="board-col">
                  <li class="board-col-name">
                    {column.title}
                    <span className="removeItemButton" onClick={(e) => this.removeColumn(column.id)}>x</span>
                  </li>
                  {Cards.map((card) => {
                    return <>
                      <li class="board-col-item">
                        <EdiText
                            showButtonsOnHover
                            submitOnEnter
                            cancelOnEscape
                            editOnViewClick={true}
                            type='text'
                            value={card.title}
                            onSave={(event) => this.modifyCard(event, card.id)}
                        />
                        <span className="removeItemButton" onClick={(e) => this.removeCard(card.id)}>x</span>
                      </li>

                    </>;
                  })}
                  <input
                            className="newCard"
                            type="text"
                            placeholder="New Card"
                            maxLength="75"
                            onKeyPress={(event) => this.newCard(event, column.id)}
                            value={this.state.inputValue}
                            onChange={this.handleChange}
                        />
                </ul>
                </Droppable>
              </>;
            })}
          </div>
          <div>
              <Link to="/boards">Back</Link>
          </div></>
          </DragDropContext>
        );
    }



}

export default Board;
