import React, { Component } from "react";
import axios from "axios";
import update from "immutability-helper";
import { Link, useParams} from "react-router-dom";
import { withRouter } from "react-router";
import EdiText from 'react-editext'

class Board extends Component {
    constructor(props) {
        super(props);
        this.state = {
          columns: [],
          cards: [],
          board: null
        };
    }

    loadColumns() {
        const link = this.props.match.params.link;
        axios 
          .get(`/api/v1/board/${link}`)
          .then((res) => {
            this.setState({ board: res.data });
            axios
            .get(`/api/v1/board_columns`, { params: {board_id: res.data.id} })
            .then((res) => {
              this.setState({ columns: res.data });
              res.data.forEach((column) => {
                axios
                .get(`/api/v1/cards`, { params: {board_column_id: column.id} })
                .then((res) => {
                    this.setState({ cards: res.data });
                  })
                })
              })
            })
    }

    reloadCards = () => {
        this.setState({ cards: [] });
        this.loadColumns();
      };

    reloadColumns = () => {
        this.setState({ columns: [] });
        this.loadColumns();
      };

    newColumn = (e, id) => {
        if (e.key === "Enter" && !(e.target.value === "")) {
            axios
              .post(`/api/v1/board_columns`, { board_column: { title: e.target.value, board_id: id} })
              .then((res) => {
                this.reloadColumns();

                this.setState({
                  inputValue: "",
                });
              })
              .catch((error) => console.log(error));
            }
    };

    newCard = (e, column_id, id) => {
        if (e.key === "Enter" && !(e.target.value === "")) {
            axios
              .post(`/api/v1/cards`, { card: { title: e.target.value, board_column_id: column_id, board_id: id} })
              .then((res) => {
                this.reloadCards();

                this.setState({
                  inputValue: "",
                });
              })
              .catch((error) => console.log(error));
            }
    };

    handleChange = (e) => {
		this.setState({inputValue: e.target.value});
	  }


    removeColumn = ( id ) => {
        axios 
            .delete(`/api/v1/board_columns/${id}`)
            .then((res) => {
                const columnIndex = this.state.columns.findIndex((x) => x.id === id);
                const columns = update(this.state.columns, {
                  $splice: [[columnIndex, 1]],
                });
                this.setState({
                    columns: columns,
                });
                this.reloadCards();

              })
              .catch((error) => console.log(error));
    };

    removeCard = ( id ) => {
        axios
            .delete(`/api/v1/cards/${id}`)
            .then((res) => {
                this.reloadCards();
              })
              .catch((error) => console.log(error));
    };


    componentDidMount() {
        this.loadColumns();
    }


    modifyCard= (value, card_id) => {
        if (!(value === "")) {
            axios
                .put(`/api/v1/cards/${card_id}`, { card: { title: value} })
                .then((res) => {
                    this.reloadCards();
                })
                .catch((error) => console.log(error));
        }
    };

    render() {
        const board_id = this.state.board.id;
        return (

            <div>
            <div className="topHeading">
              <h1>Boards</h1>
              <div className="wrapItems">
              <input
                    className="newColumn"
                    type="text"
                    placeholder="Input a New Column and Press Enter"
                    maxLength="75"
                    onKeyPress={(event) => this.newColumn(event, board_id)}
                    value={this.state.inputValue}
                    onChange={this.handleChange}
                />
                <div className="listItems">
                    {this.state.columns.map((column) => {
                    return (
                        <li className="item" column={column} key={column.board_id}>
                        <Link to="/boards">{column.title}</Link>
                        <label className="itemDisplay"></label>
                        <span className="removeItemButton" onClick={(e) => this.removeColumn(column.id)}>
                            x
                        </span>
                        </li>
                    );
                    })}
                </div>
                <div className="taskContainer">
                </div>
                <input
                    className="newColumn"
                    type="text"
                    placeholder="Input a New Card and Press Enter"
                    maxLength="75"
                    onKeyPress={(event) => this.newCard(event, 6, id)}
                    value={this.state.inputValue}
                    onChange={this.handleChange}
                />
                <div className="listItems">
                    {this.state.cards.map((card) => {
                    return (
                        <li className="item" card={card} key={card.board_column_id}>
                        <EdiText
                            showButtonsOnHover
                            buttonsAlign='before'
                            type='text'
                            value={card.title}
                            onSave={(event) => this.modifyCard(event, card.id)}
                        />
                        <span className="removeItemButton" onClick={(e) => this.removeCard(card.id)}>x</span>
                        </li>
                    );
                    })}
                </div>
                </div>
              <Link to="/boards">Back</Link>
            </div>
          </div>

        );
    }


}






export default Board;
