import React, { Component } from "react";
import axios from "axios";
import update from "immutability-helper";
import { Link, useParams} from "react-router-dom";
import { withRouter } from "react-router";
import EdiText from 'react-editext'
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import { TextInput } from "./TextInput";

export function Column({board, columns, cards, reloadColumns, newColumn, newCard, handleChange, removeColumn, removeCard, modifyCard }) 
{
  return (
    <>
    <div className="taskContainer">
            {/* <input
              className="newColumn"
              type="text"
              placeholder="Input a New Column and Press Enter"
              maxLength="75"
              onKeyPress={(event) => newColumn(event, board.id)}
              value={inputValue}
              onChange={handleChange}
            /> */}
            <TextInput newColumn/>
          </div>
            <div class="board">  
            {columns.map((column) => {
              const Cards = cards.filter((card) => card.board_column_id === column.id);
              return <>
                <ul class="board-col">
                  <li class="board-col-name">
                    {column.title}
                    <span className="removeItemButton" onClick={(e) => removeColumn(column.id)}>x</span>
                  </li>
                  {Cards.sort((a,b) => a.position - b.position).map((card) => {
                    return <>
                      <li class="board-col-item">
                        <EdiText
                            showButtonsOnHover
                            submitOnEnter
                            cancelOnEscape
                            editOnViewClick={true}
                            type='text'
                            value={card.title}
                            onSave={(event) => modifyCard(event, card.id)}
                        />
                        <span className="removeItemButton" onClick={(e) => removeCard(card.id, card.position, card.board_column_id)}>x</span>
                      </li>

                    </>;
                  })}
                  {/* <input
                            className="newCard"
                            type="text"
                            placeholder="New Card"
                            maxLength="75"
                            onKeyPress={(event) => this.newCard(event, column.id)}
                            value={this.state.inputValue}
                            onChange={this.handleChange}
                        /> */}
                </ul>
              </>;
            })}
          </div>
          <div>
              <Link to="/boards">Back</Link>
          </div>
          </>

  )
}
