import React, { Component } from "react";
import axios from "axios";
import update from "immutability-helper";
import { Link } from "react-router-dom";

class Board extends Component {
    constructor(props) {
        super(props);
        this.state = {
          columns: [],
          cards: []
        };
    }

    loadColumns() {
        axios
          .get(`/api/v1/boards/9/board_columns`)
          .then((res) => {
            this.setState({ columns: res.data });
          })
        axios
          .get(`/api/v1/boards/9/cards`)
          .then((res) => {
            this.setState({ cards: res.data });
          })
        .catch((error) => console.log(error));
    }

    reloadCards = () => {
        this.setState({ cards: [] });
        this.loadColumns();
      };

    newColumn = (e, board_id) => {
        if (e.key === "Enter" && !(e.target.value === "")) {
            axios
              .post(`/api/v1/boards/${board_id}/board_columns`, { column: { title: e.target.value } })
              .then((res) => {
                const columns = update(this.state.columns, {
                  $splice: [[0, 0, res.data]],
                });

                this.setState({
                    columns: columns,
                  inputValue: "",
                });
              })
              .catch((error) => console.log(error));
            }
    };

    handleChange = (e) => {
		this.setState({inputValue: e.target.value});
	  }


      removeColumn = (board_id, id) => {
        axios 
            .delete(`/api/v1/boards/${board_id}/board_columns/${id}`)
            .then((res) => {
                const columnIndex = this.state.columns.findIndex((x) => x.id === id);
                const columns = update(this.state.columns, {
                  $splice: [[columnIndex, 1]],
                });
                this.setState({
                    columns: columns,
                });
                this.reloadBoards();
              })
              .catch((error) => console.log(error));
    };


    componentDidMount() {
        this.loadColumns();
    }

    render() {
        return (
            <div>
            <div className="topHeading">
              <h1>Boards</h1>
              <div className="wrapItems">
              <input
                    className="newColumn"
                    type="text"
                    placeholder="Input a New Column and Press Enter"
                    maxLength="75"
                    onKeyPress={this.newColumn(9)}
                    value={this.state.inputValue}
                    onChange={this.handleChange}
                />
                <div className="listItems">
                    {this.state.columns.map((column) => {
                    return (
                        <li className="item" column={column} key={column.board_id}>
                        <Link to="/boards">{column.title}</Link>
                        <label className="itemDisplay"></label>
                        <span className="removeItemButton" onClick={(e) => this.removeColumn(9,column.id)}>
                            x
                        </span>
                        </li>
                    );
                    })}
                </div>
                <div className="taskContainer">
                </div>
                <div className="listItems">
                    {this.state.cards.map((card) => {
                    return (
                        <div className="item" card={card} key={card.board_column_id}>
                        <Link to="/boards">{card.title}</Link>
                        <label className="itemDisplay"></label>
                        </div>
                    );
                    })}
                </div>
                </div>
              <Link to="/boards">Back</Link>
            </div>
          </div>
        );
    }


}






export default Board;
