import React, { Component } from "react";
import axios from "axios";
import update from "immutability-helper";
import { Link } from "react-router-dom";

class Board extends Component {
    constructor(props) {
        super(props);
        this.state = {
          columns: [],
        };
    }

    loadColumns() {
        axios
          .get(`/api/v1/boards/18/board_columns`)
          .then((res) => {
            this.setState({ columns: res.data });
          })
          .catch((error) => console.log(error));
    }

    componentDidMount() {
        this.loadColumns();
    }

    render() {
        return (
        <div>
            <div className="topHeading">
                <h1>Boards</h1>
             </div>
            <div className="taskContainer">
            <input
                className="newBoard"
                type="text"
                placeholder="Input a New Board and Press Enter"
                maxLength="75"
                onKeyPress={this.newBoard}
                value={this.state.inputValue}
                onChange={this.handleChange}
            />
            </div>
            <div className="wrapItems">
            <ul className="listItems">
                {this.state.columns.map((column) => {
                return (
                    <li className="item" column={column} key={column.id}>
                    <Link to={`/board`}>{column.title}</Link>
                    <label className="itemDisplay"></label>
                    <span className="removeItemButton" onClick={(e) => this.removeBoard(column.id)}>
                        x
                    </span>
                    </li>
                );
                })}
            </ul>
            </div>
        </div>
        );
    }


}





