import React, { Component } from "react";

export function Column({column, removeColumn})
{
    return (
        <>
        <ul class="board-col">
                  <li class="board-col-name">
                    {column.title}
                    <span className="removeItemButton" onClick={(e) => removeColumn(column.id)}>x</span>
                  </li>
               {cards.sort((a,b) => a.position - b.position).map((card) => {
                    return <>
                      <li class="board-col-item">
                        <EdiText
                            showButtonsOnHover
                            submitOnEnter
                            cancelOnEscape
                            editOnViewClick={true}
                            type='text'
                            value={card.title}
                            onSave={(event) => modifyCard(event, card.id)}
                        />
                        <span className="removeItemButton" onClick={(e) => removeCard(card.id, card.position, card.board_column_id)}>x</span>
                      </li>

                    </>;
                  })}
                  <input
                            className="newCard"
                            type="text"
                            placeholder="New Card"
                            maxLength="75"
                            onKeyPress={(event) => this.newCard(event, column.id)}
                            value={this.state.inputValue}
                            onChange={this.handleChange}
                        /> 
                </ul>
        </>
    )
}
