import React, { Component } from "react";
import { Link, useParams} from "react-router-dom";
import { TextInput } from "./TextInput";
import {Column} from "./Column";

export function Board({board, columns, cards, reloadColumns, newColumn, newCard, handleChange, removeColumn, removeCard, modifyCard, inputValue }) 
{
  return (
    <>
    <div className="taskContainer">
            <TextInput 
                handleChange={handleChange}
                newColumn={newColumn}
                board={board}
                inputValue={inputValue}
            />
          </div>
            <div class="board">  
            {columns.map((column) => {
              const Cards = cards.filter((card) => card.board_column_id === column.id);
                return < Column
                  cards={cards}
                  column={column}
                  removeColumn={removeColumn}
                  newCard={newCard}
                  removeCard={removeCard}
                  handleChange={handleChange}
                  board={board}
                  inputValue={inputValue}
                />
            })}
          </div>
          <div>
              <Link to="/boards">Back</Link>
          </div>
          </>

  )
}
