import React, { Component } from "react";
import axios from "axios";
import update from "immutability-helper";
import { Link } from "react-router-dom";

class Board extends Component {
    constructor(props) {
        super(props);
        this.state = {
          columns: [],
          cards: []
        };
    }

    loadColumns() {
        const {
            match: {
              params: { board_id }
            }
          } = this.props;
        axios
          .get(`/api/v1/board_columns`, { params: {board_id: 4} })
          .then((res) => {
            this.setState({ columns: res.data });
          })
        axios
          .get(`/api/v1/cards`, { params: {board_id: 4} })
          .then((res) => {
            this.setState({ cards: res.data });
          })
        .catch((error) => console.log(error));
    }

    reloadCards = () => {
        this.setState({ cards: [] });
        this.loadColumns();
      };

    reloadColumns = () => {
        this.setState({ columns: [] });
        this.loadColumns();
      };



    modifyCard= (e, id) => {
        axios
          .put(`/api/v1/cards/${id}`, { tdlist: { title: e.target.checked } })
          .then((res) => {
            const tdlistIndex = this.state.tdlists.findIndex(
              (x) => x.id === res.data.id
            );
            const tdlists = update(this.state.tdlists, {
              [tdlistIndex]: { $set: res.data },
            });
            this.setState({
              tdlists: tdlists,
            });
          })
          .catch((error) => console.log(error));
    };



    newColumn = (e, id) => {
        if (e.key === "Enter" && !(e.target.value === "")) {
            axios
              .post(`/api/v1/board_columns`, { board_column: { title: e.target.value, board_id: id} })
              .then((res) => {
                this.reloadColumns();

                this.setState({
                  inputValue: "",
                });
              })
              .catch((error) => console.log(error));
            }
    };

    newCard = (e, column_id, id) => {
        if (e.key === "Enter" && !(e.target.value === "")) {
            axios
              .post(`/api/v1/cards`, { card: { title: e.target.value, board_column_id: column_id, board_id: id} })
              .then((res) => {
                this.reloadCards();

                this.setState({
                  inputValue: "",
                });
              })
              .catch((error) => console.log(error));
            }
    };

    handleChange = (e) => {
		this.setState({inputValue: e.target.value});
	  }


    removeColumn = ( id ) => {
        axios 
            .delete(`/api/v1/board_columns/${id}`)
            .then((res) => {
                const columnIndex = this.state.columns.findIndex((x) => x.id === id);
                const columns = update(this.state.columns, {
                  $splice: [[columnIndex, 1]],
                });
                this.setState({
                    columns: columns,
                });
                this.reloadCards();

              })
              .catch((error) => console.log(error));
    };

    removeCard = ( id ) => {
        axios
            .delete(`/api/v1/cards/${id}`)
            .then((res) => {
                this.reloadCards();
              })
              .catch((error) => console.log(error));
    };


    componentDidMount() {
        this.loadColumns();
    }

    render() {
        return (


            <div>
            <div className="topHeading">
              <h1>Boards</h1>
              <div className="wrapItems">
              <input
                    className="newColumn"
                    type="text"
                    placeholder="Input a New Column and Press Enter"
                    maxLength="75"
                    onKeyPress={(event) => this.newColumn(event, 4)}
                    value={this.state.inputValue}
                    onChange={this.handleChange}
                />
                <div className="listItems">
                    {this.state.columns.map((column) => {
                    return (
                        <li className="item" column={column} key={column.board_id}>
                        <Link to="/boards">{column.title}</Link>
                        <label className="itemDisplay"></label>
                        <span className="removeItemButton" onClick={(e) => this.removeColumn(column.id)}>
                            x
                        </span>
                        </li>
                    );
                    })}
                </div>
                <div className="taskContainer">
                </div>
                <input
                    className="newColumn"
                    type="text"
                    placeholder="Input a New Card and Press Enter"
                    maxLength="75"
                    onKeyPress={(event) => this.newCard(event, 6, 4)}
                    value={this.state.inputValue}
                    onChange={this.handleChange}
                />
                <div className="listItems">
                    {this.state.cards.map((card) => {
                    return (
                        <li className="item" card={card} key={card.board_column_id}>
                        <Link to="/boards">{card.title}</Link>
                        <label className="itemDisplay"></label>
                        <span className="removeItemButton" onClick={(e) => this.removeCard(card.id)}>
                            x
                        </span>
                        </li>
                    );
                    })}
                </div>
                </div>
              <Link to="/boards">Back</Link>
            </div>
          </div>

        );
    }


}






export default Board;
