import React, { Component } from "react";
import axios from "axios";
import update from "immutability-helper";
import { Link, useParams} from "react-router-dom";
import { withRouter } from "react-router";
import EdiText from 'react-editext'
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import Sortable from 'sortablejs';
import {Column} from "./Column";

class Board extends Component {
    constructor(props) {
        super(props);
        this.state = {
          columns: [],
          cards: [],
          board: null
        };
    }

    loadColumns() {
        const link = this.props.match.params.link;
        axios 
          .get(`/api/v1/board/${link}`)
          .then((res) => {
            this.setState({ board: res.data });
            axios
            .get(`/api/v1/board_columns`, { params: {board_id: res.data.id} })
            .then((res) => {
              this.setState({ columns: res.data });
              res.data.forEach((column) => {
                axios
                .get(`/api/v1/cards`, { params: {board_column_id: column.id} })
                .then((res) => {
                    const newCards = this.state.cards.concat(res.data)
                    this.setState({cards: newCards})
                  })
                })
              })
            })
    }

    reloadColumns = () => {
        this.setState({ columns: [], cards: [], board: null });
        this.loadColumns();
      };

    newColumn = (e, id) => {
        if (e.key === "Enter" && !(e.target.value === "")) {
            axios
              .post(`/api/v1/board_columns`, { board_column: { title: e.target.value, board_id: id} })
              .then((res) => {
                const columns = update(this.state.columns, {
                  $push: [[res.data]],
                });     
                this.setState({
                  columns: columns,
                  inputValue: "",
                });
                this.reloadColumns();
              })
              .catch((error) => console.log(error));
            }
    };

    newCard = (e, id) => {
        if (e.key === "Enter" && !(e.target.value === "")) {
            axios
              .post(`/api/v1/cards`, { cards: { title: e.target.value, board_column_id: id} })
              .then((res) => {
                const cards = update(this.state.cards, {
                  $push: [[0,0, res.data]],
                });

                this.setState({
                  cards: cards,
                  inputValue: "",
                });
                this.reloadColumns();
              })
              .catch((error) => console.log(error));
            }
    };

    handleChange = (e) => {
		this.setState({inputValue: e.target.value});
	  }


    removeColumn = ( id ) => {
        axios 
            .delete(`/api/v1/board_columns/${id}`)
            .then((res) => {
                const columnIndex = this.state.columns.findIndex((x) => x.id === id);
                const columns = update(this.state.columns, {
                  $splice: [[columnIndex, 1]],
                });
                this.setState({
                    columns: columns,
                });    
              })
              .catch((error) => console.log(error));
    };

    removeCard = ( id, position, column_id) => {
        axios
            .delete(`/api/v1/cards/${id}`, { cards: { position: position, board_column_id: column_id} })
            .then((res) => {
              const cardIndex = this.state.cards.findIndex((x) => x.id === id);
              const cards = update(this.state.cards, {
                $splice: [[cardIndex, 1]],
              });
              this.setState({
                cards: cards,
              });
            })
            .catch((error) => console.log(error));
    };


    componentDidMount() {
        this.loadColumns();
    }


    modifyCard= (value, id) => {
          axios
              .put(`/api/v1/cards/${id}`, { cards: { title: value} })
              .then((res) => {
                const cardIndex = this.state.cards.findIndex(
                  (x) => x.id === res.data.id
                );
                const cards = update(this.state.cards, {
                  [cardIndex]: { $set: res.data },
                });
                this.setState({
                  cards: cards,
                });
              })
              .catch((error) => console.log(error));
  };

    render() {
        return (
          <Column
              board={this.state.board}
              columns={this.state.columns}
              cards={this.state.cards}
              reloadColumns={this.reloadColumns}
              newColumn={this.newColumn}
              handleChange={this.handleChange}
              removeColumn={this.reloadColumns}
              removeCard={this.removeCard}
              modifyCard={this.modifyCard}
              />
        );
    }



}

export default Board;
