import React, { Component } from "react";
import "./App.css";
import BoardsContainer from "./components/BoardsContainer";
import Board from "./components/Board";
import Boardd from "./components/Column";
import { initBoardSortable } from './components/initSortable';
import { BrowserRouter as Router, Route, Switch, Link, useParams} from "react-router-dom";


function App() {
  return(
    <div className="mainContainer">
        <Router>
          <Switch>
            <Route path="/boards" exact component={BoardsContainer}/>
            <Route path="/board/:link" exact component={Board}/>
            <Route path="/boardd/:link" exact component={Boardd}/>
          </Switch>
        </Router>
      </div>
  );
}

document.addEventListener('turbolinks:load', () => {
  const kanbanUls = document.querySelectorAll(".board .board-col");
  if (kanbanUls) {
    initBoardSortable(kanbanUls);
  }
});


export default App;
