import React, { Component } from "react";

export function TextInput({handleChange, newColumn, board})
{
    return(
        <input
        className="newColumn"
        type="text"
        placeholder="Input a New Column and Press Enter"
        maxLength="75"
        onKeyPress={(event) => newColumn(event, board.id)}
        value={this.state.inputValue}
        onChange={handleChange}
            />
    )
}
